.resume{font-family:"Avenir Next","Apple SD Gothic Neo","Nanum Barun Gothic","Nanum Gothic",Verdana,Arial,"Malgun Gothic",Dotum,sans-serif;max-width:600px
}
@media print{
.resume{max-width:initial
}
}
.resume h1{font-size:2em;margin:0 0 0.5em
}
.resume section{display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-orient:vertical;-webkit-box-direction:normal;-ms-flex-direction:column;flex-direction:column;padding:2em 0;border-bottom:1px solid #ced4da
}
.resume section:first-child{padding-top:0
}
@media print{
.resume section{padding:0;border:none
}
.resume section:not(:last-child){page-break-after:always
}
}
.resume li:not(:last-child){margin-bottom:0.375em
}
.resume li,.resume p{line-height:1.7
}
.resume mark{background:none;border-bottom:1px solid #ff3030
}
.resume code{background-color:#f1f3f5;border-radius:4px;padding:2px
}
.resume small{color:#868e96;margin-left:4px
}
.resume .military{margin:0
}
.head{display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-orient:vertical;-webkit-box-direction:normal;-ms-flex-direction:column;flex-direction:column
}
@media print{
.head{display:none
}
}
@media screen and (min-width: 768px){
.head{-webkit-box-orient:horizontal;-webkit-box-direction:normal;-ms-flex-direction:row;flex-direction:row;-webkit-box-pack:justify;-ms-flex-pack:justify;justify-content:space-between;-webkit-box-align:center;-ms-flex-align:center;align-items:center
}
}
.head .title{font-size:3em;margin-bottom:0
}
.head .pdf{text-decoration:underline
}
.profile a{font-size:0.75em;line-height:2
}
.oneLiner{font-size:1.5em;margin:1em 0
}
.focus{font-weight:bold
}
.frontend h3{margin-bottom:0.5em
}
@media print{
.subsection{page-break-inside:avoid
}
}
.subsection:not(:nth-child(2)){margin-top:2em
}
.subsection>h2:first-child{margin-top:0
}
.educationItem:not(:nth-child(2)){margin-top:2em
}
.educationItem p{margin-top:0.5em;margin-bottom:0
}
@media print{
.experienceDetail{page-break-inside:avoid
}
}
.experienceDetail:not(:last-child){padding-bottom:1em
}
.experienceDetail>h3{margin-bottom:0.5em
}
.experienceDetail>h3+p{margin-top:0
}
.experienceDate,.educationDate{font-size:0.875em;color:#868e96;margin-top:0
}
.experienceTitle,.educationTitle{margin-bottom:0.25em
}
.gpa{margin-left:0.25em;font-size:0.75em
}
"],"sourceRoot":""}]);

