.markdown h3{font-size:1.25em
}
.markdown h4{font-size:1em
}
.markdown h5{font-size:0.875em
}
.markdown h6{font-size:0.85em;color:#6a737d
}
.markdown ul,.markdown ol{padding-left:2em
}
.markdown ul ul,.markdown ul ol,.markdown ol ol,.markdown ol ul{margin-top:0;margin-bottom:0
}
.markdown li>p{margin-top:16px
}
.markdown li+li{margin-top:0.25em
}
.markdown dl{padding:0
}
.markdown dl dt{padding:0;margin-top:16px;font-size:1em;font-style:italic;font-weight:600
}
.markdown dl dd{padding:0 16px;margin-bottom:16px
}
.markdown table{display:block;width:100%;overflow:auto
}
.markdown table th{font-weight:600
}
.markdown table th,.markdown table td{padding:6px 13px;border:1px solid #dfe2e5
}
.markdown table tr{background-color:#fff;border-top:1px solid #c6cbd1
}
.markdown table tr:nth-child(2n){background-color:#f6f8fa
}
.markdown img{max-width:100%;-webkit-box-sizing:content-box;box-sizing:content-box;background-color:#fff
}
.markdown code{padding:0;padding-top:0.2em;padding-bottom:0.2em;margin:0;font-size:85%;background-color:rgba(27,31,35,0.05);border-radius:3px
}
.markdown code::before,.markdown code::after{letter-spacing:-0.2em;content:"\00a0"
}
.markdown pre{word-wrap:normal
}
.markdown pre>code{padding:0;margin:0;font-size:100%;word-break:normal;white-space:pre;background:transparent;border:0
}
.markdown .highlight{margin-bottom:16px
}
.markdown .highlight pre{margin-bottom:0;word-break:normal
}
.markdown .highlight pre,.markdown pre{padding:16px;overflow:auto;font-size:85%;line-height:1.45;background-color:#f6f8fa;border-radius:3px
}
.markdown pre code{display:inline;max-width:auto;padding:0;margin:0;overflow:visible;line-height:inherit;word-wrap:normal;background-color:transparent;border:0
}
.markdown pre code::before,.markdown pre code::after{content:normal
}
.markdown>:first-child{margin-top:0
}
.markdown>:last-child{margin-bottom:0
}
